    public void ler() {
        DefaultTableModel dtable = (DefaultTableModel) jTable1.getModel();
        dtable.setNumRows(0);

        for (Categoria c : categoriaController.read()) {
            dtable.addRow(new Object[]{
                c.getId(),
                c.getDescricao()});
        }
    }
    
