    private void jButtonAdicionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAdicionarActionPerformed
        categoriaController.create(jTextFieldDescricao.getText());
        ler();
        jTextFieldDescricao.setText("");
        jTextFieldDescricao.requestFocus();
    }//GEN-LAST:event_jButtonAdicionarActionPerformed

    private void jButtonAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonAtualizarActionPerformed
        if (jTable1.getSelectedRow() != -1) {
            categoriaController.update(jTextFieldDescricao.getText(),
                    Integer.parseInt(jTable1.getValueAt(jTable1.getSelectedRow(), 0).toString()));
        }
        ler();
        jTextFieldDescricao.setText("");
        jTextFieldDescricao.requestFocus();
    }//GEN-LAST:event_jButtonAtualizarActionPerformed

    private void jButtonRemoverActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonRemoverActionPerformed
        if (jTable1.getSelectedRow() != -1) {
            categoriaController.delete((int) jTable1.getValueAt(jTable1.getSelectedRow(), 0));
            ler();
        } else {
            System.out.println("Selecione uma categoria");
        }
        jTextFieldDescricao.setText("");
        jTextFieldDescricao.requestFocus();
    }//GEN-LAST:event_jButtonRemoverActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        if (jTable1.getSelectedRow() != -1) {
            jTextFieldDescricao.setText(jTable1.getValueAt(jTable1.getSelectedRow(), 1).toString());
        }
    }//GEN-LAST:event_jTable1MouseClicked

